import { Box, Card, CardHeader, CardBody, Text } from "@chakra-ui/react";
import { useEffect, useState } from "react";

function CoursesPage() {
	const [backendData, setBackendData] = useState({ data: [""] });

	useEffect(() => {
		fetch("/api")
			.then((response) => response.json())
			.then((data) => {
				setBackendData(data);
			});
	}, []);

	return (
		<Box margin="10px">
			{backendData.data.map((course) => {
				return (
					<Card border="2px" borderColor="black">
						<CardHeader border="2px" borderColor="black">
							<Text>{course.name}</Text>
						</CardHeader>
						<CardBody>
							<Text>{course.desc}</Text>
						</CardBody>
					</Card>
				);
			})}
		</Box>
	);
}

export default CoursesPage;
