import {
	Box,
	Card,
	CardHeader,
	CardBody,
	Text,
	VStack,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";

function CoursesPage() {
	const [backendData, setBackendData] = useState({ data: [""] });

	useEffect(() => {
		fetch("/api")
			.then((response) => response.json())
			.then((data) => {
				setBackendData(data);
			});
	}, []);

	return (
		<Box margin="10px">
			<VStack>
				{backendData.data.map((course) => {
					return (
						<Card border="2px" borderColor="black" margin="10" width="40%">
							<CardHeader borderBottom="2px" borderColor="black">
								<Text>{course.name}</Text>
							</CardHeader>
							<CardBody>
								<Text>{course.desc}</Text>
							</CardBody>
						</Card>
					);
				})}
			</VStack>
		</Box>
	);
}

export default CoursesPage;
