import {
	Box,
	Button,
	Flex,
	Text,
	FormControl,
	FormLabel,
	Input,
	Select,
	VStack,
	useCheckbox,
	useCheckboxGroup,
} from "@chakra-ui/react";

function CourseForm() {
	function CustomCheckbox(props) {
		const { state, getCheckboxProps, getInputProps, getLabelProps, htmlProps } =
			useCheckbox(props);

		return (
			<chakra.label
				display="flex"
				flexDirection="row"
				alignItems="center"
				gridColumnGap={2}
				maxW="40"
				bg="green.50"
				border="1px solid"
				borderColor="green.500"
				rounded="lg"
				px={3}
				py={1}
				cursor="pointer"
				{...htmlProps}
			>
				<input {...getInputProps()} hidden />
				<Flex
					alignItems="center"
					justifyContent="center"
					border="2px solid"
					borderColor="green.500"
					w={4}
					h={4}
					{...getCheckboxProps()}
				>
					{state.isChecked && <Box w={2} h={2} bg="green.500" />}
				</Flex>
				<Text color="gray.700" {...getLabelProps()}>
					Click me for {props.value}
				</Text>
			</chakra.label>
		);
	}

	const { value, getCheckboxProps } = useCheckboxGroup({
		defaultValue: ["2"],
	});

	return (
		<FormControl>
			<VStack>
				<FormLabel>Name</FormLabel>
				<Input></Input>
				<FormLabel>Description</FormLabel>
				<Input></Input>
				<FormLabel>Other Teachers</FormLabel>
				<Select key="type" placeholder="Select Other Teachers"></Select>
				<Button type="submit">Submit</Button>
			</VStack>
		</FormControl>
	);
}

export default CourseForm;
