import {
	Box,
	Card,
	CardHeader,
	CardBody,
	Text,
	VStack,
	Input,
	Center,
	CardFooter,
	Button,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";

function CoursesPage(props) {
	const [backendData, setBackendData] = useState();
	const [searchField, setSearchField] = useState("");

	useEffect(() => {
		if (props.resData.found) {
			fetch("/api/courses")
				.then((response) => response.json())
				.then((data) => {
					setBackendData(data);
				});
		}
	}, [props.resData.found]);

	const handleChange = (e) => {
		setSearchField(e.target.value);
	};

	const handleClick = (e) => {
		switch (props.resData.type) {
			case "student":
				break;

			case "admin":
				break;

			case "teacher":
				break;

			default:
				break;
		}
	};

	return (
		<Center margin="10px">
			<VStack marginTop="10" width="40%" spacing="5">
				<Input
					type="search"
					placeholder="Search Courses"
					onChange={handleChange}
				/>
				{"list of courses"}
				<Box style={{ overflowY: "scroll" }} width="100%" height="500">
					{typeof backendData == "undefined" ? (
						<Center>
							<p>Loading....</p>
						</Center>
					) : (
						backendData.data
							.filter((course) => {
								return course.name
									.toLowerCase()
									.includes(searchField.toLowerCase());
							})

							.map((course) => {
								return (
									<Card border="2px" borderColor="black" marginBottom="2">
										<CardHeader borderBottom="2px" borderColor="black">
											<Text>{course.name}</Text>
										</CardHeader>
										<CardBody borderBottom="2px" borderColor="black">
											<Text>{course.desc}</Text>
										</CardBody>
										<CardFooter>
											<Button onClick={handleClick}>
												{props.resData.type === "student" ? (
													<p>Enroll</p>
												) : props.resData.type === "teacher" ? (
													<p>Edit</p>
												) : (
													<p>Delete</p>
												)}
											</Button>
										</CardFooter>
									</Card>
								);
							})
					)}
				</Box>
			</VStack>
		</Center>
	);
}

export default CoursesPage;
