import {
	Box,
	Card,
	CardHeader,
	Heading,
	CardBody,
	Text,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";

function CoursesPage() {
	const [backendData, setBackendData] = useState({
		names: [""],
		description: [""],
	});

	useEffect(() => {
		fetch("/api")
			.then((response) => response.json())
			.then((data) => {
				setBackendData(data);
			});
	}, []);

	function createCards() {
		let elements = [];

		for (let index = 0; backendData.names.length(); index++) {
			const name = backendData.names[index];
			const desc = backendData.description[index];

			return (
				<Card>
					<CardHeader>
						<Heading size="md">{name}</Heading>
					</CardHeader>

					<CardBody>
						<Box>
							<Heading size="xs" textTransform="uppercase">
								Description
							</Heading>
							<Text pt="2" fontSize="sm">
								{desc}
							</Text>
						</Box>
					</CardBody>
				</Card>
			);
		}
	}

	return (
		<Box margin="10px">
			<createCards />
		</Box>
	);
}

export default CoursesPage;
