import {
	Box,
	Card,
	CardHeader,
	CardBody,
	Text,
	VStack,
	Input,
	Center,
	CardFooter,
	Button,
	Modal,
	ModalOverlay,
	ModalContent,
	ModalHeader,
	ModalFooter,
	ModalBody,
	ModalCloseButton,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";

function AccountsPage(props) {
	const [backendData, setBackendData] = useState();
	const [searchField, setSearchField] = useState("");

	useEffect(() => {
		if (props.resData.found) {
			fetch("/api/accounts")
				.then((response) => response.json())
				.then((data) => {
					setBackendData(data);
				});
		}
	}, [props.resData.found]);

	const handleChange = (e) => {
		setSearchField(e.target.value);
	};

	const newAccount = (e) => {};

	const deleteAccount = (e) => {
		fetch("/api/deleteAccount", {
			method: "POST",
			headers: { "Content-Type": "application/json" },
			body: JSON.stringify({ id: e }),
		}).then((response) => response.json());
	};
	return (
		<Center margin="10px">
			<VStack marginTop="10" width="40%" spacing="5">
				<Button onClick={newAccount}>Create new Account</Button>
				<Input
					type="search"
					placeholder="Search Accounts"
					onChange={handleChange}
				/>
				{"list of accounts"}
				<Box style={{ overflowY: "scroll" }} width="100%" height="500">
					{typeof backendData == "undefined" ? (
						<Center>
							<p>Loading....</p>
						</Center>
					) : (
						backendData.data
							.filter((account) => {
								return account.firstname
									.toLowerCase()
									.includes(searchField.toLowerCase());
							})

							.map((account) => {
								return (
									<Card border="2px" borderColor="black" marginBottom="2">
										<CardHeader borderBottom="2px" borderColor="black">
											<Text>{account.firstname + " " + account.lastname}</Text>
										</CardHeader>
										<CardBody borderBottom="2px" borderColor="black">
											<Text>{account.type}</Text>
										</CardBody>
										<CardFooter>
											<Button
												key={account.id}
												onClick={() => {
													deleteAccount(account.id);
												}}
											>
												Delete
											</Button>
										</CardFooter>
									</Card>
								);
							})
					)}
				</Box>
			</VStack>
		</Center>
	);
}

export default AccountsPage;
