import {
	Box,
	FormControl,
	FormLabel,
	Input,
	FormHelperText,
	FormErrorMessage,
} from "@chakra-ui/react";
import { useState } from "react";

function LoginPage() {
	const [inputID, setInputID] = useState("");
	const [inputPass, setInputPass] = useState("");

	let isErrorID;
	const handleIDChange = (e) => {
		setInputID(e.target.value);
		isErrorID = inputID === "";
	};
	let isErrorPass;
	const handlePassChange = (e) => {
		setInputPass(e.target.value);
		isErrorPass = inputID === "";
	};

	return (
		<Box>
			<FormControl>
				<FormLabel>User ID</FormLabel>
				<Input value={inputID} onChange={handleIDChange} />
				{!isErrorID ? (
					<FormHelperText>Enter user ID here</FormHelperText>
				) : (
					<FormErrorMessage>UserID is required.</FormErrorMessage>
				)}

				<FormLabel>Password</FormLabel>
				<Input value={inputPass} onChange={handlePassChange} />
				{!isErrorPass ? (
					<FormHelperText>Enter Password here</FormHelperText>
				) : (
					<FormErrorMessage>Password is required.</FormErrorMessage>
				)}
			</FormControl>
		</Box>
	);
}

export default LoginPage;
