import {
	Box,
	Card,
	CardHeader,
	CardBody,
	Text,
	VStack,
	Input,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";

function CoursesPage() {
	const [backendData, setBackendData] = useState();
	const [searchField, setSearchField] = useState("");

	useEffect(() => {
		fetch("/api")
			.then((response) => response.json())
			.then((data) => {
				setBackendData(data);
			});
	}, []);

	let filteredPersons = backendData.data.filter((course) => {
		return course.name.toLowerCase().includes(searchField.toLowerCase());
	});

	const handleChange = (e) => {
		setSearchField(e.target.value);
	};

	return (
		<Box margin="10px">
			<VStack marginTop="10">
				<Input
					type="search"
					placeholder="Search People"
					onChange={handleChange}
				/>
				{typeof backendData == "undefined" ? (
					<p>Loading....</p>
				) : (
					filteredPersons.map((course) => {
						return (
							<div style={{ overflowY: "scroll", height: "70vh" }}>
								<Card border="2px" borderColor="black" width="40%">
									<CardHeader borderBottom="2px" borderColor="black">
										<Text>{course.name}</Text>
									</CardHeader>
									<CardBody>
										<Text>{course.desc}</Text>
									</CardBody>
								</Card>
							</div>
						);
					})
				)}
			</VStack>
		</Box>
	);
}

export default CoursesPage;
